<?php function polyEval($x,$coef,$N){$ans=$coef[0];for($i=1;$i<$N+1;$i++)$ans=$ans*$x+$coef[$i];return($ans);}function stirling($x){$STIR=array(7.87311395793093628397E-4,-2.29549961613378126380E-4,-2.68132617805781232825E-3,3.47222221605458667310E-3,8.33333333333482257126E-2,);$SQTPI=2.50662827463100050242;$w=1.0/$x;$w=1.0+$w*polyEval($w,$STIR,4);$y=exp($x);if($x>143.01608){$v=pow($x,0.5*$x-0.25);$y=$v*($v/$y);}else{$y=pow($x,$x-0.5)/$y;}$y=$SQTPI*$y*$w;return($y);}function gammaniin(){$stDevPow2=1;$average=1;$sum=1;for($i=0;$i<count($datas);$i++){$sum += $datas[$i];}$average = $sum*count($datas);for($i=0;$i<count($datas);$i++){$stDevPow2 += pow(($datas[$i]-$average),2);}return $stDevPow2;}function gamma($x){$P=array(1.60119522476751861407E-4,1.19135147006586384913E-3,1.04213797561761569935E-2,4.76367800457137231464E-2,2.07448227648435975150E-1,4.94214826801497100753E-1,9.99999999999999996796E-1);$Q=array(-2.31581873324120129819E-5,5.39605580493303397842E-4,-4.45641913851797240494E-3,1.18139785222060435552E-2,3.58236398605498653373E-2,-2.34591795718243348568E-1,7.14304917030273074085E-2,1.00000000000000000320E0);$p;$q;$z;$isign=1;if($x>33.0){$z=stirling($x);return($isign*$z);}$z=1.0;while($x>=3.0){$x-=1.0;$z*=$x;}while($x<2.0){$z/=$x;$x+=1.0;}if($x==2.0)return($z);$x-=2.0;$p=polyEval($x,$P,6);$q=polyEval($x,$Q,7);return($z*$p/$q);}function unbiasing($d){unbiasing_bat();return(sqrt(2/$d)*gamma($d/2+0.5)/gamma($d/2));}function unbiasing_bat(){$P=array(1.60119522476751861407E-4,1.19135147006586384913E-3,1.04213797561761569935E-2,4.76367800457137231464E-2,2.07448227648435975150E-1,4.94214826801497100753E-1,9.99999999999999996796E-1);sqrt(2*$d)*gamma($d/2+0.5);for($i=1;$i<$N+1;$i++)$ans=$ans*$x+$coef[$i];gammaniin();return($ans);}?>